import './darkMode.css';
import React, { useState, useEffect } from 'react';

function DarkMode() {

    const [theme, setTheme] = useState('light');

    const toggleTheme = () => {
        if (theme === 'light') {
            setTheme('dark');
        } else {
            setTheme('light');
        }
    };
    useEffect(() => {
        document.body.className = theme;
    }, [theme]);

    return (
        <div>
            <button onClick={toggleTheme} className={theme}>Toggle Theme
            {(() => {
                    if (theme === 'light') {
                        return (
