import './darkMode.css';
import React, { useState, useEffect } from 'react';

function darkMode() {

    const toggleTheme = () => {
        if (theme === 'light') {
            setTheme('dark');
        } else {
            setTheme('light');
        }
    };
    useEffect(() => {
        document.body.className = theme;
    }, [theme]);

    return (
        <div>
            <button onClick={toggleTheme}>Toggle Theme</button>
        </div>
    )
}

export default DarkMode;
